// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

interface IWETH {
    function deposit() external payable;
    function transfer(address to, uint256 value) external returns (bool);


}

contract SafeMoonLike is ERC20, Ownable, Pausable {
    IUniswapV2Router02 public uniswapRouter;
    address public liquidityPair;
    address public wethAddress;
    uint256 public buyTaxLiquidity = 2;  // Percentage (2%)
    uint256 public buyTaxReflection = 3; // Percentage (3%)
    uint256 public sellTaxLiquidity = 2; // Percentage (2%)
    uint256 public sellTaxReflection = 3; // Percentage (3%)

    uint256 public minTokensForDividends = 250000 * (10 ** decimals()); // 250,000 tokens minimum

    mapping(address => bool) private _isExcludedFromTax;
    mapping(address => uint256) public lastDividendClaim;

    constructor(address _router, address _weth, address initialOwner ) ERC20("SafeMoonLike", "SFM") Ownable(initialOwner)  {
        uniswapRouter = IUniswapV2Router02(_router);
        wethAddress = _weth;
        _transferOwnership(initialOwner);


        // Create liquidity pair
        liquidityPair = IUniswapV2Factory(uniswapRouter.factory()).createPair(address(this), uniswapRouter.WETH());

        _isExcludedFromTax[owner()] = true;
        _isExcludedFromTax[address(this)] = true;

        _mint(msg.sender, 1_000_000_000 * (10 ** decimals())); // Initial supply of 1 billion tokens
    }

    // Modifier to exclude tax
    modifier onlyWhenNotPaused() {
        require(!paused(), "Contract is paused");
        _;
    }

        function totalReflections() public  view returns (uint256) {
    return address(this).balance; // If the contract holds the reflections as Ether/WETH
}

    // Pausable functionality for owner
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // Handle buys and sells (tax logic)
    function transfer(address sender, address recipient, uint256 amount) internal whenNotPaused {
        if (_isExcludedFromTax[sender] || _isExcludedFromTax[recipient]) {
            super._transfer(sender, recipient, amount);
        } else {
            uint256 taxAmount;
            uint256 reflectionAmount;
            uint256 liquidityAmount;

            if (sender == liquidityPair) { // Buy
                taxAmount = (amount * (buyTaxLiquidity + buyTaxReflection)) / 100;
                liquidityAmount = (amount * buyTaxLiquidity) / 100;
                reflectionAmount = (amount * buyTaxReflection) / 100;
            } else if (recipient == liquidityPair) { // Sell
                taxAmount = (amount * (sellTaxLiquidity + sellTaxReflection)) / 100;
                liquidityAmount = (amount * sellTaxLiquidity) / 100;
                reflectionAmount = (amount * sellTaxReflection) / 100;
            }

            if (taxAmount > 0) {
                super._transfer(sender, address(this), taxAmount);
            }
            if (reflectionAmount > 0) {
                distributeReflection(reflectionAmount);
            }

            super._transfer(sender, recipient, amount - taxAmount);
        }
    }

    // Distribute reflections to eligible holders
    function distributeReflection(uint256 reflectionAmount) private {
        uint256 totalSupply = totalSupply();

        for (uint256 i = 0; i < _getHolderCount(); i++) {
            address holder = _getHolderAt(i);
            if (balanceOf(holder) >= minTokensForDividends) {
                uint256 holderReflection = (balanceOf(holder) * reflectionAmount) / totalSupply;
                IWETH(wethAddress).transfer(holder, holderReflection);
            }
        }
    }



    // Claim WETH dividends if eligible
    function claimWethDividend() external {
        require(balanceOf(msg.sender) >= minTokensForDividends, "Insufficient tokens to claim dividends");
        uint256 lastClaim = lastDividendClaim[msg.sender];
        uint256 timeElapsed = block.timestamp - lastClaim;

        // Only allow claiming once every 24 hours
        require(timeElapsed > 1 days, "Claim available only once every 24 hours");

        uint256 reflectionAmount = (balanceOf(msg.sender) * totalReflections()) / totalSupply();
        lastDividendClaim[msg.sender] = block.timestamp;

        IWETH(wethAddress).transfer(msg.sender, reflectionAmount);
    }

    // Internal function to get number of token holders (stub)
    function _getHolderCount() internal pure returns (uint256) {
        return 100; // Implement as per your contract's tracking of token holders.
    }

    function _getHolderAt(uint256 index) internal pure returns (address) {
        return address(0); // Replace with logic to get the holder's address.
    }

    // Update tax percentages
    function updateBuyTax(uint256 _liquidity, uint256 _reflection) external onlyOwner {
        buyTaxLiquidity = _liquidity;
        buyTaxReflection = _reflection;
    }

    function updateSellTax(uint256 _liquidity, uint256 _reflection) external onlyOwner {
        sellTaxLiquidity = _liquidity;
        sellTaxReflection = _reflection;
    }

    // Update the minimum tokens required to claim dividends
    function updateMinTokensForDividends(uint256 _minTokens) external onlyOwner {
        minTokensForDividends = _minTokens;
    }

    // Exclude an address from tax
    function excludeFromTax(address account) external onlyOwner {
        _isExcludedFromTax[account] = true;
    }

    // Include an address back to tax
    function includeInTax(address account) external onlyOwner {
        _isExcludedFromTax[account] = false;
    }

    // Fallback function to accept WETH
    receive() external payable {}
}

